import React, { useState, useEffect } from 'react';
import api from '../api';

export default function NotesPage() {
    const [notes, setNotes] = useState([]);
    const [text, setText] = useState('');
    const [editMode, setEditMode] = useState(false);
    const [currentNote, setCurrentNote] = useState(null);

    const loadNotes = async () => {
        const res = await api.get('/notes');
        setNotes(res.data);
    };

    const createNote = async () => {
        await api.post('/notes', { text });
        setText('');
        loadNotes();
    };

    const deleteNote = async (id) => {
        await api.delete(`/notes/${id}`);
        loadNotes();
    };

    const startEditing = (note) => {
        setText(note.text);
        setEditMode(true);
        setCurrentNote(note);
    };

    const updateNote = async () => {
        await api.put(`/notes/${currentNote.id}`, { text });
        setText('');
        setEditMode(false);
        setCurrentNote(null);
        loadNotes();
    };

    const logout = () => {
        localStorage.removeItem('token');
        window.location.reload();
    };

    useEffect(() => {
        loadNotes();
    }, []);

    return (
        <div className="notes-container">
            <h2>üìù Ghi ch√∫ c·ªßa b·∫°n</h2>
            <textarea value={text} onChange={(e) => setText(e.target.value)} />
            <br />
            <button onClick={editMode ? updateNote : createNote}>{editMode ? 'C·∫≠p nh·∫≠t' : 'Th√™m ghi ch√∫'}</button>
            <button onClick={logout}>ƒêƒÉng xu·∫•t</button>

            <ul className="note-list">
                {notes.map((note) => (
                    <li key={note.id} className="note-item">
                        {note.text}
                        <button onClick={() => startEditing(note)}>S·ª≠a</button>
                        <button onClick={() => deleteNote(note.id)}>Xo√°</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
