import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/NotesPage.css';

function NotesPage() {
    const navigate = useNavigate();
    const [notes, setNotes] = useState([]);
    const [newNote, setNewNote] = useState('');

    const fetchNotes = async () => {
        const token = localStorage.getItem('token');
        try {
            const res = await axios.get('http://localhost:5000/api/notes', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotes(res.data);
        } catch (err) {
            console.error('Failed to fetch notes:', err);
        }
    };

    const handleAddNote = async () => {
        const token = localStorage.getItem('token');
        if (!newNote.trim()) return;
        try {
            await axios.post('http://localhost:5000/api/notes', { content: newNote }, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNewNote('');
            fetchNotes();
        } catch (err) {
            console.error('Failed to add note:', err);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    useEffect(() => {
        fetchNotes();
    }, []);

    return (
            <div className="home-notes-container">
                <aside className="sidebar">
                    <header className="user-header">
                            <div className="user-info">
                                <img 
                                    src="https://via.placeholder.com/40" 
                                    alt="User Avatar" 
                                    className="user-avatar" 
                                />
                                <span className="user-name">PHAM VAN PHU</span>
                            </div>
                        </header>
                    <h3>Teamspaces</h3>
                    <ul>
                        <li>Home</li>
                        <li>Blog</li>
                        <li>List notes</li>
                        <li>..</li>
                        <li>..</li>
                    </ul>
                    <button className="invite-btn">Invite members</button>
                </aside>
        
                <main className="main-content">
                    <header className="header">
                        <h2>Good afternoon, User</h2>
                        <input type="text" placeholder="Search or find anything..." />
                    </header>

                    <section className="recent-notes">
                        <h3>Recently visited</h3>
                        <div className="notes-grid">
                            <div className="note-card">Cơ sở dữ liệu</div>
                            <div className="note-card">Frontend</div>
                            <div className="note-card">Plans</div>
                            <div className="note-card">DỰ ÁN</div>
                        </div>
                    </section>
                </main>
            </div>

        // <div className="notes-container">
        //     <div className="header">
        //         <h2>Ghi chú của bạn</h2>
        //         <button className="logout-btn" onClick={handleLogout}>Đăng xuất</button>
        //     </div>

        //     <div className="note-input">
        //         <textarea
        //             placeholder="Viết ghi chú mới..."
        //             value={newNote}
        //             onChange={(e) => setNewNote(e.target.value)}
        //         />
        //         <button className="add-btn" onClick={handleAddNote}>+ Thêm ghi chú</button>
        //     </div>

        //     <div className="notes-list">
        //         {notes.map((note) => (
        //             <div key={note.id} className="note-card">
        //                 <p>{note.content}</p>
        //             </div>
        //         ))}
        //     </div>
        // </div>
    );
}

export default NotesPage;
